mapper.select:
select id, member_email, member_name, member_phone, member_status, member_provider as provider, member_kakao_email as kakao_email, created_datetime from tbl_member where member_role = 'member'
and ( member_name like concat('%', #{search.keyword}, '%') or member_email like concat('%', #{search.keyword}, '%') or member_kakao_email like concat('%', #{search.keyword}, '%') )
order by id desc limit #{criteria.count} offset #{criteria.offset}

mapper.update:
update tbl_member
set member_status = 'inactive'
where id=#{memberId}
